class ByDefinitionTests < Test::Unit::TestCase  # :nodoc:

  def test_by
    assert_equal "Новы год", (Holidays.on(Date.civil(2017, 1, 1), [:by])[0] || {})[:name]
    assert_equal "Новы год", (Holidays.on(Date.civil(2017, 1, 2), [:by])[0] || {})[:name]

    assert_equal "Каляды", (Holidays.on(Date.civil(2017, 1, 7), [:by])[0] || {})[:name]

    assert_equal "Дзень абаронцаў Айчыны і Узброеных Сіл Рэспублікі Беларусь", (Holidays.on(Date.civil(2017, 2, 23), [:by])[0] || {})[:name]

    assert_equal "Міжнародны жаночы дзень", (Holidays.on(Date.civil(2017, 3, 8), [:by])[0] || {})[:name]
    assert_equal "Дзень Канстытуцыі", (Holidays.on(Date.civil(2017, 3, 15), [:by])[0] || {})[:name]
    assert_equal "Дзень яднання народаў Беларусі і Расіі", (Holidays.on(Date.civil(2017, 4, 2), [:by])[0] || {})[:name]

    assert_equal "Свята працы", (Holidays.on(Date.civil(2017, 5, 1), [:by])[0] || {})[:name]

    assert_equal "Дзень Перамогі", (Holidays.on(Date.civil(2017, 5, 9), [:by])[0] || {})[:name]

    assert_equal "Дзень Незалежнасці Рэспублікі Беларусь (Дзень Рэспублікі)", (Holidays.on(Date.civil(2017, 7, 3), [:by])[0] || {})[:name]
    assert_equal "Дзень народнага адзінства", (Holidays.on(Date.civil(2017, 9, 17), [:by])[0] || {})[:name]

    assert_equal "Дзень памяці", (Holidays.on(Date.civil(2017, 11, 2), [:by])[0] || {})[:name]
    assert_equal "Дзень Кастрычніцкай рэвалюцыі", (Holidays.on(Date.civil(2017, 11, 7), [:by])[0] || {})[:name]

    assert_equal "Каляды", (Holidays.on(Date.civil(2017, 12, 25), [:by])[0] || {})[:name]
  end
end
